  var assert = require('assert');
  var todo = require('./index.js');

  describe('todo', function() {
    it('todoList is an array to length 4',function() {
        assert.equal(todo.getTodo().length, 3);
    });

    it('add todo work',function() {
        todo.addTodo({idCreator:1,idAssignee:2,action:"write"});
          assert.equal(todo.getTodo().length, 4);
    });

    it('delete todo work',function() {
        todo.deleteTodo(1);
          assert.equal(todo.getTodo().length, 3);
      });
    
    it('getComplete todo work',function() {
        todo.getComplete(1);
          assert.equal(todo.getTodo().length, 3);
    });
    
    it('getNotCompleted todo work',function() {
      todo.getNotCompleted(2);
        assert.equal(todo.getTodo().length, 3);
    });

    it('users is an array to length 3',function() {
              assert.equal(todo.getUsers().length, 3);
      });
      
    it('add users work',function() {
        todo.addUsers({idUser:3,name:"Paolo"});
          assert.equal(todo.getUsers().length, 4);
    });

    it('todoUser todo work',function() {
      todo.todoUser(1);
        assert.equal(todo.todoUser().length, 2);
    });
    
  });
    